package net.jeeshop.web.action.manage.resource.mainpage;import net.jeeshop.core.Services;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.core.front.SystemManager;import net.jeeshop.core.oscache.FrontCache;import net.jeeshop.core.util.CreateAreaUtil;import net.jeeshop.services.manage.area.AreaService;import net.jeeshop.services.manage.area.bean.Area;import net.jeeshop.services.manage.catalog.CatalogService;import net.jeeshop.services.manage.catalog.bean.Catalog;import net.jeeshop.services.manage.indexImg.IndexImgService;import net.jeeshop.services.manage.indexImg.bean.IndexImg;import net.jeeshop.web.action.BaseController;import net.sf.json.JSONArray;import org.apache.commons.lang.StringUtils;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;import java.io.IOException;import java.util.ArrayList;import java.util.Collection;import java.util.List;import java.util.Map;import java.util.TreeMap;/** * 区域 * @author huangf * @author dylan * */@Controller@RequestMapping("/resource/mainpage/")public class MainPageResource extends BaseController<Area> {	private static final long serialVersionUID = 1L;	private static final org.slf4j.Logger logger = LoggerFactory.getLogger(MainPageResource.class);    private final String page_toAddOrUpdate = "/manage/area/addOrUpdate";    @Autowired	private AreaService areaService;    @Autowired	private FrontCache frontCache;        @Autowired   	private IndexImgService indexImgService;        @Autowired   	private CatalogService catalogService;        	    @Override    public Services<Area> getService() {        return areaService;    }	    /**	 * mainpage list	 * @return	 * @throws IOException 	 */    @RequestMapping("getMainPage")    @ResponseBody	public String getMainPage() throws IOException{    	    	MainPageDto returnDto = new MainPageDto();    	    	IndexImg indexImg = new IndexImg();    	List<IndexImg> indexImgList = indexImgService.selectPageList(indexImg).getList();    	returnDto.setIndexImgList(indexImgList);    	    	Catalog catalog = new Catalog();    	catalog.setPid("0");    	List<Catalog> catalogList=catalogService.selectList(catalog);    	returnDto.setCatalogList(catalogList);    			JSONArray json = JSONArray.fromObject(returnDto);		String jsonStr = json.toString();		logger.error("jsonStr="+jsonStr);		return jsonStr;	}	}